<script>
export default {
  methods: {
    changeTheme() {
      const themeCss = document.querySelector("#themeRef");
      if (themeCss.getAttribute("href") === "./vars-dark.css") {
        themeCss.setAttribute("href", "./vars-light.css");
      } else {
        themeCss.setAttribute("href", "./vars-dark.css");
      }
    },
    route(page) {
      switch (page) {
        case "orders":
          this.$router.push("/account/orders");
          break;
        case "likesList":
          this.$router.push("/account/likesList");
          break;
        case "settings":
          this.$router.push("/account/settings");
          break;
      }
    },
    toggleNotifications() {
      const btn = document.querySelector(".account__nav__item__btn__notify");
      btn.classList.toggle("account__nav__item__btn__notify_active");
    },
  },
};
</script>
